import React, { Component } from 'react';

import * as S from './styles'

import Header from '../../components/Header';

import api from '../../services/api'

export default class Home extends Component {
    constructor(props) {
        super(props)
        console.log('Meu inicializador')


        const movies = [
            {id: 0, title: "Filme1", overview: "Sinopse do filme"},
            {id: 1, title: "Filme2", overview: "Sinopse do filme 2"},
        ]

        this.state = {rows: [
        <p key="0">This is my row</p>,
        <p key="1">This is my row</p>,
        <p key="2">This is my row</p>
        ]}

        let movieRows = []
        movies.forEach((movie) => {
        console.log(movie.title)
        const movieRow = <table>
            <tbody>
                <tr>
                    <td>
                        <img src=""/>
                    </td>
                    <td>
                    {movie.title}
                    </td>
                </tr>
            </tbody>
        </table>
        movieRows.push(<p key={movie.id}>{movie.title}</p>)
        })

        this.state = {rows: movieRows}
    }

    render() {
        return (
        <S.Container>
        <Header />
        {this.state.rows}

        </S.Container>
        )
    }
}
